import React, { Component } from 'react';
import 'tachyons';
import './taskList.css';






class TaskList extends Component
{

	constructor(props)
	{
		super(props);
		this.state = {
			tasks: [
				{
					name: 'Task 1',
					description: '',
					status: ''
				},
				{
					name: 'Task 2',
					description: '',
					status: ''
				},
				{
					name: 'Task 3',
					description: '',
					status: 'completed'
				},
				{
					name: 'Task 4',
					description: '',
					status: ''
				},
				{
					name: 'Task 5',
					description: '',
					status: ''
				},
				{
					name: 'task 6',
					description: '',
					status: 'completed'
				},
				{
					name:'task 7',
					description: '',
					status: ''
				}
			]
		};
	}





	onCompleted = (index) =>{
		console.log(index);
		console.log(this.state.tasks)
		const newTasks = {...this.state.tasks}


		newTasks[index] = Object.assign(newTasks[index], {status:'completed'})

		// newTasks[index] = {...newTasks[index], status:'completed'}
		// console.log(newTasks);
		// // this.setState({tasks[index].status: 'completed'});

		// console.log(newTasks);

		this.setState({tasks: newTasks});
		console.log(this.state.tasks);


	}


	onAddTask = ()=>
	{
		console.log('add');
		this.props.onRouteChange('add');
	}



	render()
	{
		const { tasks } = this.state;
		//const filteredTasks = tasks.filter((task)=>task.status!=='completed')
		return (
			<div>
				<h1 className="pa3 tableFlex heading">TASKS</h1>
					<br/>
				<div className="tableFlex f4">
					<input 
						type="submit" 
						value="+ Add a Task"
						onClick={this.onAddTask} 
					/>
					<br/>
				</div>
				<div className="tableFlex">
					<div className="pa4">
						<div className="overflow-auto">
						    <table className="f6 w-100 mw8 center" cellSpacing="0">
						      <thead>
						        <tr>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						        </tr>
						      </thead>
						      <tbody className="lh-copy">
						      	{
						      		tasks.map((task, index) => {
						      			if(task.status!=='completed')
						      			{
						      				return (
							      				<tr key={index} className="f4">
										          <td className="pv3 pr3 bb b--black-20">{task.name}</td>
										          <td className="pv3 pr3 bb pa5 b--black-20">
										          	<input 
										          		type="submit" 
										          		value="Edit"

										          	/>
										          </td>
										          <td className="pv3 pr3 bb pa5 b--black-20">
										          	<input 
										          		type="submit" 
										          		value="Completed"
										          		onClick={()=>this.onCompleted(index)}
										          	/>
										          </td>						          
										        </tr>
						      				)
						      			}

						      		})
						      	} 
						      </tbody>
						    </table>
					  	</div>
					</div>

				</div>
			</div>
		);
	}
}

export default TaskList;
