import { 
	ADD_NEW_TASK,
	TASK_COMPLETED,
	UPDATE_TASK,
} from './constants';
let taskid=0;

export const addTask = (name,description="") => {
	return (
		{
			type: ADD_NEW_TASK,
			payload: {
				id:++taskid,
				name,
				description,
				status:"incomplete"
			}
		}
	)
}

export const taskCompleted = (id) => {
	return (
		{
			type: TASK_COMPLETED,
			payload: {
				id
			}
		}
	);
}

export const updateTask = (id,name,description) => {
	return({
		type:UPDATE_TASK,
		payload:{
			id,
			name,
			description
		}
	})
}
