import React, { Component } from 'react';
import 'tachyons';
import './taskList.css';








// <div>
// 				<h1 classNameName="pa3 heading">TASKS</h1>
// 				<div classNameName="pa3">
// 					<input type="submit" value="+ Add a Task" />
// 					<ul>
// 						<div classNameName="listflex">
// 							<li>
// 								<input type="checkbox"/>
// 								<label>Task 1</label>
// 							</li>
// 						</div>
// 						<li>
// 							<input type="checkbox"/>
// 							<label>Task 2</label>
// 						</li>
// 						<li>
// 							<input type="checkbox"/>
// 							<label>Task 3</label>
// 						</li>
// 						<li>
// 							<input type="checkbox"/>
// 							<label>Task 4</label>
// 						</li>
// 					</ul>
// 				</div>
// 			</div>


class TaskList extends Component
{

	constructor()
	{
		super();
		this.state = {
			tasks: [
				{
					name: 'Task 1',
					description: '',
					status: ''
				},
				{
					name: 'Task 2',
					description: '',
					status: ''
				},
				{
					name: 'Task 3',
					description: '',
					status: ''
				},
				{
					name: 'Task 4',
					description: '',
					status: ''
				},
				{
					name: 'Task 5',
					description: '',
					status: ''
				}
			]
		};
	}


	render()
	{
		const { tasks } = this.state;
		const filteredTasks = tasks.filter((task)=>task.name!=='completed')
		return (
			<div>
				<h1 className="pa3 tableFlex heading">TASKS</h1>
					<br/>
				<div className="tableFlex f4">
					<input type="submit" value="+ Add a Task" />
					<br/>
				</div>
				<div className="tableFlex">
					<div className="pa4">
						<div className="overflow-auto">
						    <table className="f6 w-100 mw8 center" cellspacing="0">
						      <thead>
						        <tr>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						        </tr>
						      </thead>
						      <tbody className="lh-copy">
						      	{
						      		tasks.filter((task)=>{
						      			return (
						      				<tr className="f4">
									          <td className="pv3 pr3 bb b--black-20">{task.name}</td>
									          <td className="pv3 pr3 bb pa5 b--black-20">
									          	<input type="submit" value="Edit"/>
									          </td>
									          <td className="pv3 pr3 bb pa5 b--black-20">
									          	<input type="submit" value="Completed"/>
									          </td>						          
									        </tr>
						      			)
						      		})
						      	}
						        <tr className="f4">
						          <td className="pv3 pr3 bb b--black-20">Task 1</td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Edit"/>
						          </td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Completed"/>
						          </td>						          
						        </tr>
						        <tr className="f4">
						          <td className="pv3 pr3 bb b--black-20">Task 2</td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Edit"/>
						          </td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Completed"/>
						          </td>
						        </tr>
						        <tr className="f4">
						          <td className="pv3 pr3 bb b--black-20">Task 3</td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Edit"/>
						          </td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Completed"/>
						          </td>
						        </tr>
						        <tr className="f4">
						          <td className="pv3 pr3 bb b--black-20">Task 4</td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Edit"/>
						          </td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Completed"/>
						          </td>
						        </tr>
						        <tr className="f4">
						          <td className="pv3 pr3 bb b--black-20">Task 5</td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Edit"/>
						          </td>
						          <td className="pv3 pr3 bb pa5 b--black-20">
						          	<input type="submit" value="Completed"/>
						          </td>
						        </tr>
						      </tbody>
						    </table>
					  	</div>
					</div>

				</div>
			</div>
		);
	}
}

export default TaskList;
