import React, { Component } from 'react';
import { connect } from 'react-redux';
import 'tachyons';
import './completedTasks.css';




class CompletedTasks extends Component
{



	onDeleteTask = (id)=>
	{	
		console.log(id);
	}


	render()
	{
		const tasks = this.props.tasks;
		return(
			<div>
				<div>
					<h1 className="pa3 main">Completed TASKS</h1>
					<input type="submit" value="<Back" />
				</div>
				<div className="tableFlex">
					<div className="pa4">
						<div className="overflow-auto">
						    <table className="f6 w-100 mw8 center" cellSpacing="0">
						      <thead>
						        <tr>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						          <th className="fw6 bb b--black-20 tl pb3 pr3 bg-white"></th>
						        </tr>
						      </thead>
						      <tbody className="lh-copy">
						      	{
						      		tasks.map((task, index) => {
						      			return (
							      				<tr key={index} className="f4">
										          <td className="pv3 pr3 bb b--black-20">{task.name}</td>
										          <td className="pv3 pr3 bb pa5 b--black-20">
										          	<input 
										          		type="submit" 
										          		value="Delete"
										          		onClick={()=>this.onDeleteTask(task.id)}
										          	/>
										          </td>						          
										        </tr>
						      			)

						      		})
						      	} 
						      </tbody>
						    </table>
					  	</div>
					</div>
				</div>
			</div>
		);
	}
}


const mapStateToProps = (state) => {
	console.log(state);
	return{
		tasks: state.tasks.filter(task => task.status === 'completed')
	}
}

const mapDispatchToProps = (dispatch) =>{
	return{
		hello:''
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(CompletedTasks);
