import {
	ADD_NEW_TASK,
	TASK_COMPLETED,
	UPDATE_TASK,
	FETCH_TASK,
} from './constants.js';


const initialState={
	tasks:[
		{
			id:54, 
			name: 'Task 1',
			description: '',
			status: 'incomplete'
		},
		{
			id:55,
			name: 'Task 2',
			description: '',
			status: 'incomplete'
		},
		{
			id:56,
			name: 'Task 3',
			description: '',
			status: 'completed'
		},
		{
			id:57,
			name: 'Task 4',
			description: '',
			status: 'incomplete'
		},
		{
			id:58,
			name: 'Task 5',
			description: '',
			status: 'incomplete'
		},
		{
			id:59,
			name: 'task 6',
			description: '',
			status: 'completed'
		},
		{
			id:60,
			name:'task 7',
			description: '',
			status: 'incomplete'
		}
	],
	toUpdate: [
	]
}

export const taskManagement = (state=initialState, action={})=>{
	switch(action.type)
	{
		case ADD_NEW_TASK:
			return {...state,tasks:[...state.tasks,action.payload]}
		case TASK_COMPLETED:
			console.log("action payload = ",action.payload);
			let task = state.tasks.filter(task=>task.id===action.payload.id)
			console.log("1",task);
			task[0].status = "completed";
			console.log("Task Completed", task);
			const otherTasks = state.tasks.filter(task=>task.id!==action.payload)
			return {...state,tasks:[...otherTasks,task[0]]}
		case UPDATE_TASK:
			// console.log("inReducer",action.payload);
			let allTasks = state.tasks.filter(task=> task.id !== action.payload.id)
			allTasks = [...allTasks,action.payload];
			// console.log("Tasks",allTasks);
			console.log("Before", state);
			state.toUpdate.pop();
			console.log("After", state);
		case FETCH_TASK:
			console.log("In Reducer",action.payload.id);
			let taskToUpdate = state.tasks.filter(task=>task.id===action.payload.id)
			// console.log(taskToUpdate[0]);
			const newState = {...state, toUpdate:[taskToUpdate]}
			// console.log("Before",newState);
			return newState;
		default:
			return state;
	}
}
