import {ADD_NEW_TASK_NAME} from './constants.js';


const initialStateTaskName={
	tasks:[]
	// name: '',
	// description: '',
	// status: ''
}

export const taskName = (state=initialStateTaskName, action={})=>{
	switch(action.type)
	{
		case ADD_NEW_TASK_NAME:
			return Object.assign({},state,{name: action.payload});
		default:
			return state;
	}
}
