import React, { Component } from 'react';
import 'tachyons';
import './updateTask.css';
import { connect } from 'react-redux';
class UpdateTask extends Component {



	render(){
		// console.log(this.props);
		console.log(this.props.task);
		return(
			<div>
				<div className='main pa3'>
					<article className="br3 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw6 center shadow-5">
						<main className="pa4 black-80">
						  <div className="measure ">
						    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
						      <legend className="f2 fw6 ph0 mh0">Edit Details</legend>
						      <div className="mt3">
						        <label className="db fw6 lh-copy f3" htmlFor="email-address">Task Name</label>
						        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
						        	type="text" 
						        	name="task-name"  
						        	id="task-name"

						        />
						      </div>
						      <div className="mt3">
						        <label className="db fw6 lh-copy f3" htmlFor="email-address">Description</label>
						        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
						        	type="text" 
						        	name="task-name"  
						        	id="task-name"
						        />
						      </div>
						    </fieldset>
						    <div className="">
							    <input 
							        onClick={this.onSubmit}
							        className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f3 dib"
							        type="submit"
							        value="Update"
							        onClick={this.onAdd}
							    />
						    </div>
						  </div>
						</main>
					</article>
				</div>
			</div>
		);
	}
}

const mapStateToProps = (state) =>{
	return 
	{
		task: state.toUpdate[0]
	}
}






export default connect(mapStateToProps, null)(UpdateTask);
