import {ADD_NEW_TASK} from './constants.js';


const initialState={
	tasks:[
		{
					name: 'Task 1',
					description: '',
					status: 'incomplete'
				},
				{
					name: 'Task 2',
					description: '',
					status: 'incomplete'
				},
				{
					name: 'Task 3',
					description: '',
					status: 'completed'
				},
				{
					name: 'Task 4',
					description: '',
					status: 'incomplete'
				},
				{
					name: 'Task 5',
					description: '',
					status: ''
				},
				{
					name: 'task 6',
					description: '',
					status: 'completed'
				},
				{
					name:'task 7',
					description: '',
					status: ''
				}
	],
	// name: '',
	// description: '',
	// status: ''
}

export const taskName = (state=initialState, action)=>{
	switch(action.type)
	{
		case ADD_NEW_TASK:
			return {...state,tasks:[...state.tasks,action.payload]}
		default:
			return state;
	}
}
