import React, { Component } from 'react';
import './App.css';
import TaskList from './Components/TaskList/taskList';
import AddTask  from './Components/AddTask/addtask';
import UpdateTask from './Components/updateTask/updateTask';
import { addTask } from './actions';







class App extends Component{
	constructor()
	{
		super();
		this.state = {
			route: 'home',
			taskId: 0,
			taskName: ''
		}
	}

	onUpdatingTask = (id,name) =>{
		console.log("Here",id,name);
		function updateDetails(state,props)
		{
			console.log("Checking:",name);
			console.log("In Function:", state);
			return {...state, taskName: name + state.taskName}
		}		
		this.setState(updateDetails);
		// this.setState({taskId: id,taskName: name});
		console.log(this.state);	
	}

	onRouteChange = (route) =>{
		if(route == 'add')
			this.setState(Object.assign({},this.state,{route: 'add'}));
		else if(route === 'home')
			this.setState(Object.assign({},this.state,{route: 'home'}));
		else if(route === 'update')
			this.setState(Object.assign({},this.state,{route: 'update'}));
	}


	render()
	{
		return (
			<div>
				{
					(this.state.route === 'home')
					?	<TaskList onRouteChange = {this.onRouteChange} onUpdatingTask={this.onUpdatingTask}/>
				    : (
				    	this.state.route === 'add'
				    	?	<div><AddTask onRouteChange = {this.onRouteChange} onNameChange= {this.props.onNameChange}/></div>
						: 	<div><UpdateTask onRouteChange= {this.onRouteChange} id={this.state.taskId} name={this.state.taskName}/></div>  	
				    )
				}

			</div>
		);
	}
}

export default App;
